import java.util.regex.Pattern; public class EmailTest { public static void main(String[] args) { Pattern p = Pattern.compile("^[A-Za-z0-9+_.-]+@([A-Za-z0-9.-]+\\.[A-Za-z]{2,})$"); String[] emails = {"invalid-email","@example.com","user@","user@.com","user@com","user..double.dot@example.com","user@example.","user@.example.com","user name@example.com","user@exam ple.com","user@","@",""}; for(String email : emails) { System.out.println(email + " -> " + p.matcher(email).matches()); } }}
